INSERT INTO `core_template` (`template_name`, `template_value`) VALUES
	('core_first_alert_mail', 'Bonjour ${first_name} ! Votre compte utilisateur arrive à expiration. Pour prolonger sa validité, veuillez <a href="${url}">cliquer ici</a>.</br>Si vous ne le faites pas avant le ${date_valid}, il sera désactivé.'),
	('core_expiration_mail', 'Bonjour ${first_name} ! Votre compte a expiré. Vous ne pourrez plus vous connecter avec, et les données vous concernant ont été anonymisées'),
	('core_other_alert_mail', 'Bonjour ${first_name} ! Votre compte utilisateur arrive à expiration. Pour prolonger sa validité, veuillez <a href="${url}">cliquer ici</a>.</br>Si vous ne le faites pas avant le ${date_valid}, il sera désactivé.'),
	('core_account_reactivated_mail', 'Bonjour ${first_name} ! Votre compte utilisateur a bien été réactivé. Il est désormais valable jusqu\'au ${date_valid}.'),
	('core_password_expired', 'Bonjour ! Votre mot de passe a expiré. Lors de votre prochaine connection, vous pourrez le changer.'),
	('extend_rating_vote_type_star', '<#assign averageScore = 0 />\r\n<#assign voteCount = 0 />\r\n<#if rating??>\r\n	<#assign averageScore = rating.averageScore />\r\n	<#assign voteCount = rating.voteCount />\r\n</#if>\r\n<#if show == "all" || show == "vote">\r\n	<p>\r\n		<img src="images/local/skin/plugins/extend/modules/rating/stars_${averageScore!}.png" alt="Note" title="Note" />\r\n		(${voteCount!})&nbsp;\r\n	</p>\r\n</#if>\r\n<#if canDeleteVote><div class="cancel"><a href="jsp/site/plugins/extend/modules/rating/DoCancelVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}" > </a></div></#if>\r\n<#if show == "all" || show == "actionVote">\r\n	<#if canVote>\r\n		<div> Votez : </div>\r\n		<div class="resource-vote-star-rating" style="display:none;">\r\n			<form name="resource_vote_form" action="jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType}" method="post" >\r\n				<input type="hidden" name="voteValue" value="-2" />\r\n				<input class="star-rating" type="radio" name="voteValue_${extendableResourceType}_${idExtendableResource!}" value="-2" />\r\n				<input class="star-rating" type="radio" name="voteValue_${extendableResourceType}_${idExtendableResource!}" value="-1" />\r\n				<input class="star-rating" type="radio" name="voteValue_${extendableResourceType}_${idExtendableResource!}" value="1" />\r\n				<input class="star-rating" type="radio" name="voteValue_${extendableResourceType}_${idExtendableResource!}" value="2" />\r\n			</form>\r\n		</div>\r\n		<div class="resource-vote-star-rating-javascript-disable" >\r\n			<div class="star">\r\n				<a href="jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}&voteValue=-2" ></a>\r\n			</div >\r\n			<div class="star">\r\n				<a href="jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}&voteValue=-1" > </a>\r\n			</div>\r\n			<div class="star">\r\n				<a href="jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}&voteValue=1" > </a>\r\n			</div>\r\n			<div class="star">\r\n				<a href="jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}&voteValue=2" > </a>\r\n			</div>\r\n		</div>\r\n		<br/>\r\n	</#if>\r\n</#if>'),
	('extend_rating_vote_type_thumb', '\r\n<#assign scoreValue = 0 />\r\n<#assign voteCount = 0 />\r\n<#if rating??>\r\n	<#assign scoreValue = rating.scoreValue />\r\n	<#assign voteCount = rating.voteCount />\r\n</#if>\r\n<#if show == "all" || show == "vote">\r\n	<p>\r\n		<#if ( scoreValue < 0 )>\r\n			<img src="images/local/skin/plugins/extend/modules/rating/vote_against.png" title="#i18n{module.extend.rating.rating.labelVoteAgainst}" alt="#i18n{module.extend.rating.rating.labelVoteAgainst}"/>\r\n			${scoreValue!}\r\n		<#else>\r\n			<img src="images/local/skin/plugins/extend/modules/rating/vote_for.png" title="#i18n{module.extend.rating.rating.labelScore}" alt="#i18n{module.extend.rating.rating.labelScore}"/>\r\n			${scoreValue!}\r\n		</#if>\r\n		(${voteCount!})&nbsp;\r\n	</p>\r\n</#if>\r\n<#if canDeleteVote><div class="cancel"><a href="jsp/site/plugins/extend/modules/rating/DoCancelVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}" > </a></div></#if>\r\n<#if show == "all" || show == "actionVote">\r\n	<#if canVote>\r\n		<div>\r\n			<div class="extend-rating-vote-title">\r\n				<label for="">#i18n{module.extend.rating.rating.labelVote} :</label></div>\r\n				<span class="extend-rating-vote-span">\r\n					<a href="jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&amp;extendableResourceType=${extendableResourceType!}&amp;voteValue=-1">\r\n						<img src="images/local/skin/plugins/extend/modules/rating/vote_against.png" title="#i18n{module.extend.rating.rating.labelVoteAgainst}" alt="#i18n{module.extend.rating.rating.labelVoteAgainst}"/> \r\n					</a>\r\n					<a href="jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&amp;extendableResourceType=${extendableResourceType!}&amp;voteValue=1">\r\n						<img src="images/local/skin/plugins/extend/modules/rating/vote_for.png" title="#i18n{module.extend.rating.rating.labelVoteFor}" alt="#i18n{module.extend.rating.rating.labelVoteFor}"/>\r\n					</a>\r\n				</span>\r\n		</div>\r\n	</#if>\r\n</#if>\r\n');
