
DELETE FROM core_template WHERE template_name = 'extend_rating_vote_type_thumb' ;
INSERT INTO `core_template` VALUES ('extend_rating_vote_type_thumb','<#assign scoreValue = 0 />\r\n<#assign voteCount = 0 />\r\n<#if rating??>\r\n	<#assign scoreValue = rating.scoreValue />\r\n	<#assign voteCount = rating.voteCount />\r\n	<#assign scoreVotesPositifs = rating.scorePositifsVotes > \r\n	<#assign scoreVotesNegatives = rating.scoreNegativesVotes >\r\n</#if>\r\n\r\n<div class=\"row\">\r\n    <div class=\"col-xs-6 text-center\" style=\"background-color:green;color:white;\">\r\n        <#if show == \"all\" || show == \"vote\">\r\n        <p>${scoreVotesPositifs!}</p>\r\n        </#if>\r\n        <#if (show == \"all\" || show == \"actionVote\") && canVote>\r\n           <a href=\"jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}&voteValue=1\" style=\"color: inherit\">J\'aime</a>\r\n        <#else>\r\n           <p>J\'aime</p>\r\n        </#if>\r\n    </div>\r\n    <div class=\"col-xs-6 text-center\" style=\"background-color:green;color:white;\">\r\n        <#if show == \"all\" || show == \"vote\">\r\n            <p>${scoreVotesNegatives!}</p>\r\n        </#if>\r\n        <#if (show == \"all\" || show == \"actionVote\") && canVote>\r\n           <a href=\"jsp/site/plugins/extend/modules/rating/DoVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}&voteValue=-1\" style=\"color: inherit\">J\'aime pas</a>\r\n        <#else>\r\n           <p>J\'aime pas</p>\r\n        </#if>\r\n    </div>\r\n</div>\r\n<#if canDeleteVote>\r\n<div class=\"row\">\r\n    <div class=\"col-xs-12 text-center\">\r\n        <a class=\"btn btn-primary\" href=\"jsp/site/plugins/extend/modules/rating/DoCancelVote.jsp?idExtendableResource=${idExtendableResource!}&extendableResourceType=${extendableResourceType!}\" role=\"button\">Annuler mon vote</a>\r\n    </div>\r\n</div>\r\n</#if>\r\n\r\n');
